          <div class="text-center mt-3">
            <v-btn @click="open('null')" variant="text" color="primary" size="small" class="br-20 px-5">  ...</v-btn>
          </div>
        </v-col>

        <v-col cols="12" md="6" lg="6">

          <div class="ms-3" v-if="!mapNow.region">
            <v-alert
              icon="$info"
              color="primary"
              variant="tonal"
              :elevation="4"
              class="mb-3 w-100 small py-3"
              text="   "
            />
          </div>

          <div class="ml-3" v-if="mapNow.region">
            <h1 class="mb-4">{{ mapNow.region }}</h1>

            <div class="mb-4"><b> </b>: {{ mapNow.projectCount }} .</div>

            <div class="mb-1"><b>  </b>:
              <span v-if="mapNow.investPlanSum">{{ mapNow.investPlanSum.toLocaleString('ru-RU', {'maximumFractionDigits': 2}) }} . </span>

            </div>
            <div class="mb-4"><b>  </b>:
              <span v-if="mapNow.investFactSum">{{ mapNow.investFactSum.toLocaleString('ru-RU', {'maximumFractionDigits': 2}) }} . </span>
            </div>

            <div class="mb-1"><b>   </b>:
              <span v-if="mapNow.jobsPlanSum">{{ mapNow.jobsPlanSum.toLocaleString('ru-RU', {'maximumFractionDigits': 2}) }} .</span>
            </div>
            <div class="mb-2"><b>   </b>:
              <span v-if="mapNow.jobsPlanSum">{{ mapNow.jobsPlanSum.toLocaleString('ru-RU', {'maximumFractionDigits': 2}) }} .</span>
            </div>

            <div class="mt-5" v-if="  mapNow.projectCount > 0">
              <v-btn @click="open(regionNow)" variant="tonal" color="primary" class="br-20 px-5" size="small"></v-btn>
            </div>


          </div>
        </v-col>
      </v-row>
    </div>

    <v-progress-linear
      v-if="!isLoading"
      :indeterminate="true"
      color="primary"
      class="mb-0"
    />

    <v-dialog
      v-model="dialogShow"
      max-width="700"
    >
      <v-card class="br-10 " elevation="5">
        <!--  -->
        <v-card-text>
          <v-progress-linear
            v-if="!isLoadingDialog"
            :indeterminate="true"
            color="primary"
            class="mb-0"
          />


          <div
            v-for="(project, i) in dialogData"
            :key="i"
            class=" row-border align-center"
          >
            <router-link class="text-decoration-none text-black  align-center px-0 py-2 h-100 d-md-flex"
                         :to="{name:'ProjectDv', params:{id:project.id}} " target="_blank">
              <div class="px-3 pb-1 pt-2 pt-mb-1 text-center">
                <v-avatar v-if="project.avatar" :image="project.avatar" class="me-3">
                </v-avatar>
                <v-avatar v-else :color="project.avatarColor"  class="me-3">
                  {{ project.avatarText }}
                </v-avatar>
              </div>

              <div class="px-3 py-1 ">
                <p>{{ project.projectName }}</p>
                <p v-if="project.innName !== ' - '"><small>{{ project.innName }}</small></p>
              </div>
            </router-link>
          </div>


        </v-card-text>
      </v-card>
    </v-dialog>


  </v-card>
</template>

<script>
import LOCAL_CONFING from "@/LOCAL_CONFING";

export default {
  data() {
    return {
      isLoading: true,
      isLoadingDialog: false,
      dialogShow: false,
      dialogData: [],
      alertText: '',
      regionNow: '',
      mapData: {},
      mapNow: {},
      url: LOCAL_CONFING.urls.regionMap
    }
  },

  methods: {

    async open(id) {
      this.dialogShow = true;
      this.isLoadingDialog = false;
      try {
        const response = await fetch(`${this.url}?id=${id}`, {method: 'get'});
        if (response.status === 200) {
          this.dialogData = await response.json();
        } else {
          this.alertText = '    .   '
        }
      } catch (e) {
        this.alertText = '    .   '
      }
      this.isLoadingDialog = true;
    },

    selectRegion(id) {
      this.mapNow = {}
      this.regionNow = id;
      if (this.mapData[id]) {
        this.mapNow = this.mapData[id];
      }
    },

    isActiveClass(id) {
      if (id === this.regionNow) {
        return 'active'
      }
      return ''
    },

    async getMap() {
      this.isLoading = false
      this.alertText = ''
      try {
        const response = await fetch(this.url, {method: 'get'});
        if (response.status === 200) {
          this.mapData = await response.json();
        } else {
          this.alertText = '    .   '
        }
      } catch (e) {
        this.alertText = '    .   '
      }
      this.isLoading = true
    }
  },

  async created() {
    await this.getMap();
    this.selectRegion('reg-24')
  },
}
</script>

<style scoped lang="scss">

.region-map {
  filter: drop-shadow(16px 7px 8px #ddd);

  .region {
    fill: #999;
    stroke: #fff;
    stroke-width: 30;
    cursor: pointer;
  }

  .region:hover, .region.active {
    fill: #842199;
    stroke: #000;
    transition: fill 0.5s;
  }
}

.row-border {
  border-bottom: 1px solid #ddd;

  &:hover {
    background-color: #f5f5f5;
  }
}


</style>
